import { useSelector, useDispatch } from "react-redux";
import { toast } from 'react-hot-toast';
import {add, remove} from '../redux/Slices/CartSlice'

const Product = ({post}) => {

  const {cart} = useSelector((state) => state);
  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(add(post));
    toast.success("Item added to cart");
  }

  const removeFromCart = () => {
    dispatch(remove(post.id));
    toast.error("Item removed from cart");
  }
  return (
    <div className="rounded-xl flex flex-col items-center justify-between 
    hover:scale-110 transition duration-300 ease-in shadow-[0_3px_10px_rgb(0,0,0,0.2)] gap-3 p-4 mt-10 ml-5 ">
        <div>
          <p className="text-gray-700 font-semibold text-lg text-left truncate w-40 mt-1">{post.title}</p>
        </div>
        <div>
          <p className="text-gray-700 font-normal w-40 text-[10px] text-left">{post.description.split(" ").slice(0,10).join(" ") + "..."}</p>
        </div>
        <div className="h-[180px]">
          <img src={post.image} className="h-full w-ful"/>
        </div>
        <div className="flex ">
          <p>{post.price}</p>
          {
            cart.some((p) => p.id === post.id) ? <button onClick={removeFromCart}>Remove Item</button> : <button onClick={addToCart}>Add to Cart</button>
          }
        </div>
    </div>
  )
};

export default Product;
