import { createSlice } from '@reduxjs/toolkit';

const CartSlice = createSlice({
    name: 'Cart',
    initialState: [],
    reducers: {
        add: (state, action) => {
            state.push(action.payload); // action.payload represents item that we've passed as parameter in product c

        },
        remove: () => {}
    }
});

export const { add, remove } = CartSlice.actions;
export default CartSlice.reducer;
